import React, { Component } from 'react';
import './App.css';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';



class App extends Component {
  constructor() {
    super();
    this.state = {
      title:'Simple Country',
      countries: []
    }
  }

  addCountry(event) {
    event.preventDefault();
    console.log("heyho");
    let data = {
      country_name: this.refs.country_name.value,
      continent_name: "continent2",
    }
    var request = new Request("http://localhost:3000/api/new-country", {
      method: 'POST',
      headers: new Headers({'Content-Type': 'application/json'}),
      body: JSON.stringify(data)
    });

    fetch(request)
    .then(function(response) {
      response.json()
      .then(function(data) {
        console.log(data)
      })
    })
    .catch(function(err) {
      console.log(err);
    })
  }
  render() {
    return (
      <div className="App">
      <AppBar position="static">
             <Toolbar>
               <IconButton color="inherit">
                 <MenuIcon />
               </IconButton>
               <Button color="inherit" className=".">Login</Button>
             </Toolbar>
       </AppBar>
      <form ref="countryForm">
      <input type="text" ref="country_name" placeholder="countryname"/>
      <input type="text" ref="continent_name" placeholder="countryname"/>
      <button onClick={this.addCountry.bind(this)}> Add Country </button>
      </form>
      </div>
    );
  }
}

export default App;
